import React, { useState } from "react";
import { Card, Image, Tabs, Tooltip } from "antd";
import { Link } from "react-router-dom";
import { HeartOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import ProductListItems from "./ProductListItems";
import { style } from "glamor";
import StarRating from "react-star-ratings";
import RatingModal from "../modal/RatingModal";
import { showAverage } from "../../functions/rating";
import { useSelector, useDispatch } from "react-redux";
import _ from "lodash";
import { addToWishlist } from "../../functions/user";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";

const { TabPane } = Tabs;
const fallback =
  "data:image/png;base64,";

// Children component of product page
const SingleProduct = ({ product, onStarClick, star }) => {
  const [tooltip, setTooltip] = useState("Click to add");
  const { title, images, description, _id } = product;
  const textCustom = {
    changeText: style({
      fontSize: "1.15rem",
      letterSpacing: "5.25%",
      fontWeight: "lighter",
    }),
  };
  const { user, cart } = useSelector((state) => ({ ...state }));
  const dispatch = useDispatch();
  let history = useHistory();

  // save product to local storage (checkout)
  const handleAddToCart = () => {
    let cart = [];

    if (typeof window !== "undefined") {
      //get cart if already in local storage
      if (localStorage.getItem("cart")) {
        cart = JSON.parse(localStorage.getItem("cart"));
      }

      //push new product to cart
      cart.push({
        ...product,
        count: 1,
      });
      // remove duplicate product
      let unique = _.uniqWith(cart, _.isEqual);
      // save to local storage
      localStorage.setItem("cart", JSON.stringify(unique));
      setTooltip("Added");

      dispatch({
        type: "ADD_TO_CART",
        payload: unique,
      });

      //show sidedrawer
      dispatch({
        type: "SET_VISIBLE",
        payload: true,
      });
    }
  };

  const handleAddToWishlist = (e) => {
    e.preventDefault();

    addToWishlist(product._id, user.token).then((res) => {
      console.log("ADDED TO WISHLIST", res.data);
      toast.success("Added to wishlist");
      history.push("/user/wishlist");
    });
  };

  return (
    <>
      {console.log(product)}
      <div className="col-md-7">
        {images && images.length ? (
          <Carousel showArrows={true} autoPlay infiniteLoop>
            {images &&
              images.map((image) => (
                <img src={image.url} key={image.public_id} />
              ))}
          </Carousel>
        ) : (
          <div className="text-center">
            <Image
              src={"error"}
              width="100%"
              height="300px"
              style={{
                objectFit: "cover",
              }}
              fallback={fallback}
            />
          </div>
        )}
        <div className="p-5">
          <Tabs type="card">
            <TabPane tab="Description" key="1">
              <p {...textCustom.changeText}>{description && description}</p>
            </TabPane>
            <TabPane tab="More" key="2">
              <p {...textCustom.changeText}>
                Have any question about this product? <br />
                Contact us: +62-XXX-XXXX-XXXX
              </p>
            </TabPane>
          </Tabs>
        </div>
      </div>

      <div className="col-md-5">
        <h3 style={{ marginLeft: "43px" }}>{title}</h3>
        {product && product.ratings && product.ratings.length > 0 ? (
          <div className="py-2" style={{ marginLeft: "43px" }}>
            {showAverage(product)}
          </div>
        ) : (
          <p className="font-weight-light" style={{ marginLeft: "43px" }}>
            No rating yet
          </p>
        )}
        <Card
          bordered={false}
          actions={[
            <Tooltip title={tooltip}>
              <a onClick={handleAddToCart}>
                <ShoppingCartOutlined className="text-info" />
                <br />
                Add to Cart
              </a>
            </Tooltip>,
            <a onClick={handleAddToWishlist}>
              <HeartOutlined className="text-info" />
              <br /> Add to Wishlist
            </a>,
            <RatingModal>
              <StarRating
                name={_id}
                numberOfStars={5}
                rating={star}
                isSelectable={true}
                changeRating={onStarClick}
                starRatedColor="#ffcc29"
                starDimension="30px"
                starSpacing="2px"
                starHoverColor="#ffcc29"
              />
            </RatingModal>,
          ]}
        >
          <ProductListItems product={product} />
        </Card>
      </div>
    </>
  );
};

export default SingleProduct;
